import requestsapi_key = 'c39140481581401d9154beb84ed8f1c5'def get_weather(city):    base_url = "http://api.openweathermap.org/data/2.5/weather?"    city_name = city    complete_url = (base_url + "appid="                               "" + api_key + "&q=" + city_name + "&lang=ru")    response = requests.get(complete_url)    weather_data = response.json()    if weather_data["cod"] != "404":        main = weather_data["main"]        temperature = int(main["temp"] - 273.15)        weather_description = weather_data["weather"][0]["description"]        return temperature, weather_description    else:        return "Город не найден"