import loggingfrom celery import shared_taskfrom music_api.connect_service import download_tracklogger = logging.getLogger(__name__)@shared_taskdef update_chart_data():    try:        download_track()        logger.info("Трек дня успешно обновлен")        return "Трек дня обновлен"    except Exception as e:        logger.error(f"Ошибка при обновлении трека дня: {str(e)}")        return "Ошибка при обновлении трека дня"